    test('getUserCollection is working when given a username', function (){
      var tester = GameFactory.getUserCollection('mattgrosso');

      assert.ok(tester.then, 'test does return a promise');

      tester
        .then(function (response) {
          assert.isOk(response.length, 'the data returned has a length');
          assert.isOk(mockLocalStorage.collection.length, 'collection in localStorage has length');
          assert.isOk(mockLocalStorage.genreArray, 'genreArray is on localStorage');
        });
        $httpBackend.flush();
    });

    test('getUserCollection works when $localStorage is filled already', function (){
      var tester = GameFactory.getUserCollection('mattgrosso');
      mockLocalStorage.collection = [1, 2, 3, 4, 5];

      assert.ok(tester.then, 'test does return a promise');

      tester
        .then(function (response) {
          assert.isOk(response.length, 'the function returns an array');
          assert.strictEqual(response, mockLocalStorage.collection, 'the collection is coming out of localStorage');
          assert.isOk(mockLocalStorage.genreArray, 'genreArray is on localStorage');
        });
    });

    test('getUserCollection errors are handled', function (){
      var tester = GameFactory.getUserCollection(null);

      assert.ok(tester.then, 'test returns a promise');

      tester
        .then(function () {
          assert.ok(false, 'this should have failed');
        })
        .catch(function (err, status) {
          assert.strictEqual(status, 500, 'service fn does reject the promise on http failure');
        });


    });
